import "./singlePost.css";
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
import { useEffect, useState, useContext } from "react";
import { Context } from "../../context/Context";
import axios from "axios";

const SinglePost = () => {
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({});
  const publicFolder = "https://react-blog-123.herokuapp.com/images/";
  const { user } = useContext(Context);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [updateMode, setUpdateMode] = useState(false);

  // Fetch the post's id
  useEffect(() => {
    const fetchPost = async () => {
      const res = await axios.get("/posts/" + path);
      setPost(res.data);
      setTitle(res.data.title);
      setDesc(res.data.desc);
    };
    fetchPost();
  }, [path]);

  // Update Post function
  const handleUpdate = async (e) => {
    try {
      await axios.put(`/posts/${post._id}`, {
        data: { username: user.username, title, desc },
      });
      window.location.reload();
    } catch (error) {}
  };

  // Delete Post function
  const handleDelete = async (e) => {
    try {
      await axios.delete(`/posts/${post._id}`, {
        data: { username: user.username },
      });
      window.location.replace("/");
    } catch (error) {}
  };

  return (
    <div className="singlePost">
      <div className="singlePostWrapper">
        {post.photo && (
          <img
            src={publicFolder + post.photo}
            alt="Post"
            className="singlePostImg"
          />
        )}
        {/* For update or simple mode: */}
        {updateMode ? (
          <input
            type="text"
            value={title}
            className="singlePostTitleInput"
            autofocus
            onChange={(e) => setTitle(e.target.value)}
          />
        ) : (
          <h1 className="singlePostTitle">
            {post.title}
            {/* Buttons for edit/delete post */}
            {post.username === user?.username && (
              <div className="singlePostEdit">
                <i
                  className="singlePostIcon far fa-edit"
                  onClick={() => setUpdateMode(true)}
                ></i>
                <i
                  className="singlePostIcon far fa-trash-alt"
                  onClick={handleDelete}
                ></i>
              </div>
            )}
          </h1>
        )}
        <div className="singlePostInfo">
          <span className="singlePostAuthor">
            Author:{" "}
            <Link to={`/?user=${post.username}`} className="link">
              <b>{post.username}</b>
            </Link>
          </span>
          <span className="singlePostDate">1 hour ago</span>
        </div>
        {updateMode ? (
          <textarea
            className={"singlePostDescriptionInput"}
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
          />
        ) : (
          <p className="singlePostDescription">{post.desc}</p>
        )}
        <button className="singlePostButton" onClick={handleUpdate}>
          Update
        </button>
      </div>
    </div>
  );
};

export default SinglePost;
